var o=document.querySelector(".js-pop-up-feedback");var t=o.querySelector("form");var c=o.querySelector("[name=email]");var i=o.querySelector("[name=client]");document.querySelector(".js-popup-close").addEventListener("click",function(e){e.preventDefault();o.classList.remove("active")});document.querySelector(".js-feedback-button").addEventListener("click",function(e){e.preventDefault();o.classList.add("active");o.classList.remove("pop-up-error");t.querySelector("input[type='text']:first-of-type").focus()});t.addEventListener("submit",function(e){if(!c.value||!i.value){e.preventDefault();o.classList.remove("pop-up-error");o.offsetWidth=o.offsetWidth;o.classList.add("pop-up-error")}});window.addEventListener("keydown",function(e){if(e.keyCode===27){e.preventDefault();if(o.classList.contains("active")){o.classList.remove("active")}}});